mapper.select:
select s.fid fid,s.fspe_num fwpsnum,fselect,farc,fmaterial,fdiameter,finitial fini,fgas,fcontroller,s.fcharacter fcharacter,s.ftime,s.fhysteresis,s.fadvance,s.fini_ele ,s.fini_vol,s.fini_vol1,s.fweld_ele, s.fweld_vol,s.fweld_vol1,s.farc_ele,s.farc_vol,s.farc_vol1,s.fweld_tuny_ele,s.fweld_tuny_vol,s.farc_tuny_ele,farc_tuny_vol,fpreset_ele_top,fpreset_vol_top,fpreset_ele_bottom,fpreset_vol_bottom,farc_vol_top,fpreset_ele_warn_top,fpreset_vol_warn_top,fpreset_ele_warn_bottom,fpreset_vol_warn_bottom,fini_ele_warn_top,fini_vol_warn_top,fini_ele_warn_bottom,fini_vol_warn_bottom,farc_ele_warn_top,farc_vol_warn_top,farc_ele_warn_bottom,farc_vol_warn_bottom,farc_delay_time,fwarn_delay_time,fwarn_stop_time,fflow_top,fflow_bottom,fdelay_time,fover_time,ffixed_cycle, d1.fvaluename selectname,d2.fvaluename gasname,d3.fvaluename dianame,d4.fvaluename materialname,d5.fvaluename conname,d6.fvaluename finitial,d7.fvaluename arcname FROM tb_specification s LEFT JOIN tb_dictionary d1 ON s.fselect=d1.fvalue and d1.ftypeid = 10 LEFT JOIN tb_dictionary d2 ON s.fgas=d2.fvalue and d2.ftypeid=24 LEFT JOIN tb_dictionary d3 ON s.fdiameter=d3.fvalue and d3.ftypeid = 23 LEFT JOIN tb_dictionary d4 ON s.fmaterial=d4.fvalue and d4.ftypeid=18 LEFT JOIN tb_dictionary d5 ON s.fcontroller=d5.fvalue and d5.ftypeid = 19 LEFT JOIN tb_dictionary d6 ON s.finitial=d6.fvalue and d6.ftypeid=20 LEFT JOIN tb_dictionary d7 ON s.farc=d7.fvalue and d7.ftypeid=21 where 1=1
AND s.fwpslib_id=#{parent}
ORDER by s.fspe_num

select tb_mainwps.FID fid,tb_mainwps.FWPSNum fwpsnum,tb_mainwps.Fweld_I fweld_i,tb_mainwps.Fweld_V fweld_v,tb_mainwps.Fweld_I_MAX fweld_i_max,tb_mainwps.Fweld_I_MIN fweld_i_min,tb_mainwps.Fweld_V_MAX fweld_v_max,tb_mainwps.Fweld_V_MIN fweld_v_min,tb_mainwps.Fweld_Alter_I fweld_alter_i,tb_mainwps.Fweld_Alter_V fweld_alter_v,tb_mainwps.Fweld_PreChannel fweld_prechannel,tb_mainwps.FCReateDate fcreatedate,tb_mainwps.FUpdateDate fupdatedate,tb_mainwps.Fowner fowner,tb_mainwps.Fback fback,tb_mainwps.Fname fname,tb_mainwps.Fdiameter fdiameter,i.fid insid,i.fname insname from tb_mainwps left join tb_insframework i on tb_mainwps.Fowner=i.fid where 1=1

	and tb_insframework.fid = #{parent}


	and ${str}

 ORDER by tb_mainwps.FID

select s.`fid` fid,s.`fspe_num` welderid,s.finitial finitial,s.fcontroller fcontroller,s.fselect insname,s.farc weldername,s.fcharacter fweld_v_max,s.fmode fmode,s.fmaterial updatename,s.fgas fback,s.fdiameter fname,s.ftime,s.fhysteresis,s.fadvance,s.fini_ele ,s.fini_vol,s.fini_vol1,s.fweld_ele,s.fweld_vol,s.fweld_vol1,s.farc_ele,s.farc_vol,s.farc_vol1,s.fweld_tuny_ele,s.fweld_tuny_vol,s.farc_tuny_ele,s.farc_tuny_vol fdiameter FROM tb_specification s 
where 1=1 AND s.`fmachine_id`=#{machine} AND s.`fspe_num`=#{chanel}
ORDER by s.fid

select s.`fid` fid,s.`fspe_num` welderid,s.finitial finitial,s.fcontroller fcontroller,s.fselect insname,s.farc weldername,s.fcharacter fweld_v_max,s.fmode fmode,s.fmaterial updatename,s.fgas fback,s.fdiameter fname,s.ftime,s.fhysteresis,s.fadvance,s.fini_ele ,s.fini_vol,s.fini_vol1,s.fweld_ele,s.fweld_vol,s.fweld_vol1,s.farc_ele,s.farc_vol,s.farc_vol1,s.fweld_tuny_ele,s.fweld_tuny_vol,s.farc_tuny_ele,s.farc_tuny_vol fdiameter FROM tb_specification s 
where 1=1 AND s.`fmachine_id`=#{machine}

	AND s.`fspe_num`=#{chanel}

ORDER by s.fid

select s.`fid` fid,s.`fspe_num` fweld_i_max,s.finitial fweld_i_min,s.fcontroller fweld_alter_i,s.fselect fweld_i,s.farc fweld_v,s.fcharacter fweld_v_max,s.fmode fweld_v_min,s.fmaterial fweld_prechannel,s.fgas fweld_alter_v,s.fdiameter insid,s.ftime,s.fhysteresis,s.fadvance,s.fini_ele ,s.fini_vol,s.fini_vol1,s.fweld_ele,s.fweld_vol,s.fweld_vol1,s.farc_ele,s.farc_vol,s.farc_vol1,s.fweld_tuny_ele,s.fweld_tuny_vol,s.farc_tuny_ele,s.farc_tuny_vol fdiameter FROM tb_specification s 
where 1=1 AND s.`fmachine_id`=#{machine}

	AND s.`fspe_num`=#{chanel}

ORDER by s.fid

select tb_mainwps.FWPSNum fwpsnum,tb_welding_machine.fequipment_no fname,tb_wps_welddevices.Fchannel fweld_prechannel,tb_wps_welddevices.FCReateDate fcreatedate from tb_wps_welddevices left join tb_mainwps on tb_wps_welddevices.FWeldWPSID=tb_mainwps.FID left join tb_welding_machine on tb_wps_welddevices.FWeldDevicesID=tb_welding_machine.fid where 1=1

	and tb_insframework.fid = #{parent}

ORDER by tb_wps_welddevices.FCReateDate desc

SELECT wl.fid,`fwps_lib_name` fwpsnum,`fmachinemodel` macid,di.fvaluename fname,`fcreatedate` fcreatedate,`fstatus` fstatus,d.fvaluename insname,em.ftype conname FROM tb_wps_library wl LEFT JOIN tb_dictionary d ON wl.fstatus=d.fvalue and d.ftypeid=6 LEFT JOIN tb_dictionary di ON wl.fmachinemodel=di.fvalue AND di.ftypeid=17 LEFT JOIN tb_equipment_manufacturer em ON wl.fmachinemodel = em.ftype_value where 1=1
AND s.`fwps_lib_name`=#{search}
ORDER by fid

SELECT s.fid,fspe_num fwpsnum,fsolder_layer,fweld_bead,fweld_method,fmaterial,d.fvaluename materialname,fdiameter,di.fvaluename dianame,fweld_ele+fweld_tuny_ele fpreset_ele_top,fweld_ele-fweld_tuny_ele fpreset_ele_bottom, fweld_vol+fweld_tuny_vol fpreset_vol_top,fweld_vol-fweld_tuny_vol fpreset_vol_bottom,fpower_polarity,fgas_flow,fweld_speed,fwelding_process fprocessid,dic.fvaluename fprocessname,fwpslib_id insid,l.fwps_lib_name insname FROM `tb_specification` s LEFT JOIN tb_dictionary d on d.fvalue=s.fmaterial and d.ftypeid=9 LEFT JOIN tb_dictionary di on di.fvalue=s.fdiameter and di.ftypeid=13 LEFT JOIN tb_dictionary dic on dic.fvalue=s.fwelding_process and dic.ftypeid=22 LEFT JOIN tb_wps_library l on l.fid=s.fwpslib_id WHERE 1=1
AND s.`fwpslib_id`=#{parent}
ORDER by s.fweld_bead

SELECT wm.fid,wm.fequipment_no insname,wm.fmodel macid,wl.fwps_lib_name fwpsnum,wmh.fspe_num insid,wmh.fweld_ele,wmh.fweld_vol,wmh.fwarn_ele_up,wmh.fwarn_ele_down,wmh.fwarn_vol_up,wmh.fwarn_vol_down,wmh.fpreset_ele_warn_top,wmh.fpreset_vol_warn_top,wmh.fpreset_ele_warn_bottom,wmh.fpreset_vol_warn_bottom,wmh.fcreate_time updatename FROM tb_wps_machine_history wmh 
LEFT JOIN tb_welding_machine wm ON wmh.fmachine_id=wm.fid
LEFT JOIN tb_wps_library wl ON wmh.fwpslib_id=wl.fid
WHERE 1=1

	AND wl.fwps_lib_name like ${wpslibName}


	AND wm.fequipment_no like ${machineNum}


	
		and fcreate_time >= #{dto.dtoTime1}
	
	
		and fcreate_time <= #{dto.dtoTime2}
	

ORDER BY wmh.fcreate_time DESC

select s.`fid` fid,s.`fspe_num` welderid,s.finitial finitial,s.fcontroller fcontroller,s.fselect insname,s.farc weldername,s.fcharacter fweld_v_max,s.fmode fmode,s.fmaterial updatename,s.fgas fback,s.fdiameter fname,s.ftime,s.fhysteresis,s.fadvance,s.fini_ele ,s.fini_vol,s.fini_vol1,s.fweld_ele,s.fweld_vol,s.fweld_vol1,s.farc_ele,s.farc_vol,s.farc_vol1,s.fweld_tuny_ele,s.fwarn_ele_up,s.fwarn_ele_down,s.fwarn_vol_up,s.fwarn_vol_down,s.fweld_tuny_vol,s.farc_tuny_ele,s.farc_tuny_vol fdiameter,dm.fvaluename materialname,ds.fvaluename selectname,dg.fvaluename gasname,da.fvaluename arcname,dd.fvaluename dianame,s.fwater_cooled_torch ftorch,s.fwelding_process fprocessid,dp.fvaluename fprocessname FROM tb_wps_machine_history s LEFT JOIN tb_dictionary dm ON s.fmaterial=dm.fvalue and dm.ftypeid=9 LEFT JOIN tb_dictionary ds ON s.fselect=ds.fvalue and ds.ftypeid=10 LEFT JOIN tb_dictionary da ON s.farc=da.fvalue and da.ftypeid=11 LEFT JOIN tb_dictionary dg ON s.fgas=dg.fvalue and dg.ftypeid=12 LEFT JOIN tb_dictionary dd ON s.fdiameter=dd.fvalue and dd.ftypeid=13 LEFT JOIN tb_dictionary dp ON s.fwelding_process=dp.fvalue AND dp.ftypeid=22 where 1=1
AND s.`fmachine_id`=#{machineId}
AND s.`fspe_num`=#{chanel}
AND s.`fcreate_time`=#{time}

select s.fid fid,s.fspe_num fwpsnum,fselect,farc,fmaterial,fdiameter,finitial fini,fgas,fcontroller,s.fcharacter fcharacter,s.ftime,s.fhysteresis,s.fadvance,s.fini_ele ,s.fini_vol,s.fini_vol1,s.fweld_ele, s.fweld_vol,s.fweld_vol1,s.farc_ele,s.farc_vol,s.farc_vol1,s.fweld_tuny_ele,s.fweld_tuny_vol,s.farc_tuny_ele,farc_tuny_vol,fpreset_ele_top,fpreset_vol_top,fpreset_ele_bottom,fpreset_vol_bottom,farc_vol_top,fpreset_ele_warn_top,fpreset_vol_warn_top,fpreset_ele_warn_bottom,fpreset_vol_warn_bottom,fini_ele_warn_top,fini_vol_warn_top,fini_ele_warn_bottom,fini_vol_warn_bottom,farc_ele_warn_top,farc_vol_warn_top,farc_ele_warn_bottom,farc_vol_warn_bottom,farc_delay_time,fwarn_delay_time,fwarn_stop_time,fflow_top,fflow_bottom,fdelay_time,fover_time,ffixed_cycle, d1.fvaluename selectname,d2.fvaluename gasname,d3.fvaluename dianame,d4.fvaluename materialname,d5.fvaluename conname,d6.fvaluename finitial,d7.fvaluename arcname FROM tb_wps_machine_history s LEFT JOIN tb_dictionary d1 ON s.fselect=d1.fvalue and d1.ftypeid = 10 LEFT JOIN tb_dictionary d2 ON s.fgas=d2.fvalue and d2.ftypeid=24 LEFT JOIN tb_dictionary d3 ON s.fdiameter=d3.fvalue and d3.ftypeid = 23 LEFT JOIN tb_dictionary d4 ON s.fmaterial=d4.fvalue and d4.ftypeid=18 LEFT JOIN tb_dictionary d5 ON s.fcontroller=d5.fvalue and d5.ftypeid = 19 LEFT JOIN tb_dictionary d6 ON s.finitial=d6.fvalue and d6.ftypeid=20 LEFT JOIN tb_dictionary d7 ON s.farc=d7.fvalue and d7.ftypeid=21 where 1=1
AND s.`fmachine_id`=#{machineId}
AND s.`fspe_num`=#{chanel}
AND s.`fcreate_time`=#{time}

select s.* FROM tb_specification s 
where 1=1 AND s.fmachine_id=#{machine}

	AND s.fspe_num=#{chanel}

ORDER by s.fid

select s.fspe_num fwpsnum,s.f024 FROM tb_specification s 
where 1=1 AND s.fmachine_id=#{machine}
ORDER by s.fspe_num

mapper.update:
update tb_specification set 
fspe_num=#{fwpsnum},fmaterial=#{fmaterial},fdiameter=#{fdiameter},fweld_ele=#{fweld_ele},fweld_vol=#{fweld_vol},
fweld_tuny_ele=#{fweld_tuny_ele},fweld_tuny_vol=#{fweld_tuny_vol},fpower_polarity=#{fpower_polarity},
fweld_speed=#{fweld_speed},fgas_flow=#{fgas_flow},fweld_method=#{fweld_method},fwelding_process=#{fprocessid} 
where fwpslib_id=#{fwpslib_id} and fsolder_layer=#{fsolder_layer} and fweld_bead=#{fweld_bead};

mapper.select:
SELECT fid insid,fwps_lib_name insname FROM tb_wps_library WHERE 1=1 AND fid not in (
SELECT DISTINCT fwpslib_id FROM tb_welded_junction WHERE 1=1 and fwpslib_id is not NULL 
)

SELECT fid,fproduct_drawing_no,fproduct_name,fproduct_version,fwps_lib_name fwpsnum,fwps_lib_version,flag,fstatus,fback FROM tb_wps_library

	where ${search}

ORDER by fid

SELECT fid,femployee_id,femployee_version FROM tb_employee

	where fwps_lib_id=#{fid}

group by femployee_id,femployee_version ORDER by fid

SELECT fstep_number,fid FROM tb_step

	where femployee_id=#{employeeId}

group by fstep_number ORDER by fid

SELECT fjunction,fid FROM tb_junction

	where fstep_id=#{stepId}

group by fjunction ORDER by fid

SELECT fid,fquantitative_project,frequired_value,fupper_deviation,flower_deviation,funit_of_measurement FROM tb_specification

	where fstep_id=#{stepId}

ORDER by fid
